//login componet - used to login user

import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { AuthServiceService } from '../../auth-service.service';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit{

  //constructor
  constructor (public authservice: AuthServiceService, private router: Router, private fb: FormBuilder) {}

  loginForm!: FormGroup;

  //initialization
  ngOnInit(): void {
    this.loginForm = this.fb.group({
      enteredUsername: ['', [Validators.required, Validators.minLength(1)]],
      enteredPassword: ['', [Validators.required, Validators.minLength(1)]]
    });
  }

  get enteredUsername() {
    return this.loginForm.get('enteredUsername');
  }

  get enteredPassword() {
    return this.loginForm.get('enteredPassword');
  }

  get usernameValidationMessage() {
    if (this.enteredUsername?.touched && this.enteredUsername?.errors?.['required']) {
      return 'Username is required';
    }
    return '';
  }

  get passwordValidationMessage() {
    if (this.enteredPassword?.touched && this.enteredPassword?.errors?.['required']) {
      return 'Password is required';
    }
    return '';
  }


  //login
  onlogin() {
    if (this.loginForm.invalid) {
      if (!this.loginForm.value.enteredUsername || !this.loginForm.value.enteredPassword) {
        alert('Please fill in all the fields');
      } else {
        alert('Invalid');
      }
      return;
    } else {
      this.authservice.login(this.loginForm.value.enteredUsername, this.loginForm.value.enteredPassword);
    }
  }
}
<!--HTML for Login-->
<div class="d-flex justify-content-between align-items-center">

  <!--Title-->
  <h1>Login</h1>

  <!--Sign Up Botton-->
  <button class="btn btn-primary" routerLink="/signup">Sign Up</button>

</div>
<hr>

<form [formGroup]="loginForm" (ngSubmit)="onlogin()" class="input-width">

  <!--Username-->
  <div class="form-group">
      <label class="col-form-label mt-4" for="enteredUsername">Username</label>
      <input type="text"
      formControlName="enteredUsername"
      id="enteredUsername"
      placeholder="Enter username"
      required
      minlength="1"
      class="form-control"/>
  </div>
  <div *ngIf="usernameValidationMessage" class="text-danger">{{ usernameValidationMessage }}</div>

  <!--Password-->
  <div class="form-group">
      <label class="col-form-label mt-4" for="enteredPassword">Password</label>
      <input type="password"
      formControlName="enteredPassword"
      id="enteredPassword"
      placeholder="Enter password"
      required
      minlength="1"
      class="form-control"/>
  </div>
  <div *ngIf="passwordValidationMessage" class="text-danger">{{ passwordValidationMessage }}</div>

  <br>

  <!--Login Button-->
  <button type="submit" class="btn btn-primary">Login</button>
</form>
