//login componet - used to login user

import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { AuthServiceService } from '../../auth-service.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit{

  //constructor
  constructor (public authservice: AuthServiceService, private router: Router) {}

  //initialization
  ngOnInit(): void {}

  //login
  onlogin(loginform: NgForm)
  {
    //if entered data invalid
    if (loginform.invalid)
    {
      alert('Invalid')
      return
    }
    else
    {
      //login valid
      this.authservice.login(loginform.value.enteredUsername, loginform.value.enteredPassword)
    }
  }
}
<!--HTML for Login-->
<div class="d-flex justify-content-between align-items-center">

  <!--Title-->
  <h1>Login</h1>

  <!--Sign Up Button-->
  <button class="btn btn-primary" routerLink="/signup">Sign Up</button>

</div>
<hr>

<form (submit)="onlogin(loginform)" #loginform="ngForm" class="input-width">

  <!--Username-->
  <div class="form-group">
      <label class="col-form-label mt-4" for="enteredUsername">Username</label>
      <input type="text"
      ngModel name="enteredUsername"
      id="enteredUsername"
      placeholder="Enter username"
      required
      minlength="1"
      class="form-control"
      #enteredUsername="ngModel"/>
      <div *ngIf="enteredUsername.errors['required']" class="text-danger">
        Username is required and must be at least 1 character long.
      </div>
  </div>

  <!--Password-->
  <div class="form-group">
      <label class="col-form-label mt-4" for="enteredPassword">Password</label>
      <input type="password"
      ngModel name="enteredPassword"
      id="enteredPassword"
      placeholder="Enter password"
      required
      minlength="1"
      class="form-control"
      #enteredPassword="ngModel"/>
      <div *ngIf="enteredPassword.errors['required']" class="text-danger">
        Password is required and must be at least 1 character long.
      </div>
  </div>

  <br>

  <!--Login Button-->
  <button type="submit" class="btn btn-primary">Login</button>

</form>
