import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { AuthServiceService } from '../../auth-service.service';

@Component({
  selector: 'app-signup',
  templateUrl: './signup.component.html',
  styleUrls: ['./signup.component.css']
})
export class SignupComponent implements OnInit{

  constructor (public authservice: AuthServiceService, private router: Router) {}

  option: string = this.router.url

  ngOnInit(): void {}

  onsignup(signupform: NgForm)
  {
    if (signupform.invalid)
    {
      return
    }

    if (this.option == '/signup')
    {
      this.authservice.signup(
        signupform.value.enteredUsername,
        signupform.value.enteredPassword,
        signupform.value.enteredFirstname,
        signupform.value.enteredLastname,
        signupform.value.enteredContact
      )
    }
  }
}
