import { Component, OnInit } from '@angular/core';
import { AuthServiceService } from './auth/auth-service.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit{
  title = 'APDS7311';
  username: string | null = '';

  constructor(public authService: AuthServiceService, private router: Router) { }

  ngOnInit(): void {
    this.authService.username$.subscribe(username => {
      this.username = username; // update the username whenever it changes
    });

    if (this.authService.isLoggedIn()) {
      this.router.navigate(['/home']);
    }
  }
}

<!--HTML for app-->
<nav class="navbar navbar-expand bg-dark" data-bs-theme="dark">
  <div class="container-fluid">
    <a class="navbar-brand" routerLink="/home">Bulletin Board</a>
    <div id="navbarColor01">
      <ul class="navbar-nav me-auto">
      </ul>
      <ul class="navbar-nav ml-auto">
        <li *ngIf="authService.isLoggedIn()" class="nav-item">
          <span class="navbar-text mr-2">Welcome, {{username}} </span>
          <button class="btn btn-danger" (click)="authService.logout()">Sign Out</button>
        </li>
      </ul>
    </div>
  </div>
</nav>
<main>
  <div>
    <router-outlet></router-outlet>
  </div>
</main>
