import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import { SignupServiceService } from '../signup-service.service';

@Component({
  selector: 'app-signup',
  templateUrl: './signup.component.html',
  styleUrls: ['./signup.component.css']
})
export class SignupComponent{

  constructor(public signupservice: SignupServiceService) {}

  // ngOnInit(): void {
  // }

  onsignup(signupform: NgForm){
    if (signupform.invalid)
    {
      alert('Invalid')
      return
    }
    alert(signupform.value.enteredUsername)
    this.signupservice.addUsert_service(
      signupform.value.enteredUsername,
      signupform.value.enteredPassword,
      signupform.value.enteredFirstname,
      signupform.value.enteredLastname,
      signupform.value.enteredContact
      )
    signupform.resetForm()
  }

}
