import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { TokenService } from './token.service';
import { Router } from '@angular/router';
import { BehaviorSubject } from 'rxjs'

@Injectable({
  providedIn: 'root'
})
export class AuthServiceService {

  private token!: string;
  private usernameSubject = new BehaviorSubject<string | null>(null);
  username$ = this.usernameSubject.asObservable();

  constructor(private http: HttpClient, private tokenService: TokenService, private router: Router) { }

  signup (uname:string, upass:string, ufirst:string, ulast:string, ucont:string)
  {
    this.http.post('https://localhost:3000/api/user', {
      username:uname,
      password:upass,
      firstname:ufirst,
      lastname:ulast,
      contact:ucont
    }, {responseType: 'text'})
    .subscribe(response =>{
      this.router.navigate(['/login']); // navigate to LoginComponent after successful signup
    });
  }

  login (uname:string, upass:string)
  {
    this.http.post<{token:string}>('https://localhost:3000/api/auth',{
      username:uname,
      password:upass
    })
    .subscribe(respose =>{
      const token = respose.token;
      this.tokenService.setToken(token);
      localStorage.setItem('username', uname); // save the username
      this.usernameSubject.next(uname); // update the username observable
      this.router.navigate(['/home']); // navigate to PostDisplayComponent after successful login
    })
  }

  isLoggedIn() {
    return this.tokenService.getToken() != null;
  }

  logout() {
    this.tokenService.removeToken();
    localStorage.removeItem('username'); // remove the username
    this.usernameSubject.next(null); // update the username observable
    this.router.navigate(['/login']); // navigate to LoginComponent after successful logout
  }
}
